import React, { useState, useEffect } from "react";
import { getOrCreateUserId } from "../utils/user";

const Profile = ({ user, setUser, onLogout, onBackHome }) => {
  const [displayName, setDisplayName] = useState("");
  const [leaderboardOptIn, setLeaderboardOptIn] = useState(false);
  const [saving, setSaving] = useState(false);
  const [saveMessage, setSaveMessage] = useState("");

useEffect(() => {
  if (!user) return;

  const savedDisplayName =
    localStorage.getItem("displayName") || user.displayName || user.name || "";
  const savedOptIn = localStorage.getItem("leaderboardOptIn");

  setDisplayName(savedDisplayName);
  setLeaderboardOptIn(savedOptIn === "true");

  if (typeof setUser === "function") {
    setUser((prevUser) => {
      if (!prevUser) return prevUser;
      return {
        ...prevUser,
        displayName: savedDisplayName,
        leaderboardOptIn: savedOptIn === "true",
      };
    });
  }
}, [user, setUser]);

async function saveSettings(name, optIn) {
    setSaving(true);
    setSaveMessage("");
    setUser({
      ...user,
      displayName: name,
      leaderboardOptIn: optIn,
    });


    try {
      const userId = getOrCreateUserId();
      await fetch("https://read-racer.azurewebsites.net/api/updateUserSettings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId,
          userName: name,
          leaderboardOptIn: optIn,
        }),
      });

      // Save locally
      localStorage.setItem("displayName", name);
      localStorage.setItem("leaderboardOptIn", optIn.toString());

      // Update global user object
      const updatedUser = { ...user, displayName: name, leaderboardOptIn: optIn };
      setUser(updatedUser);
      localStorage.setItem("user", JSON.stringify(updatedUser));

      setSaveMessage("Settings saved!");
    } catch (e) {
      console.error("Failed to save settings:", e);
      setSaveMessage("Failed to save settings. Please try again.");
    } finally {
      setSaving(false);
      setTimeout(() => setSaveMessage(""), 3000);
    }
  }

  const onDisplayNameChange = (e) => {
    const val = e.target.value;
    setDisplayName(val);
    saveSettings(val, leaderboardOptIn);
  };

  const onLeaderboardOptInChange = (e) => {
    const checked = e.target.checked;
    setLeaderboardOptIn(checked);
    saveSettings(displayName, checked);
  };

  if (!user) {
    return (
      <div className="min-h-screen flex flex-col justify-center items-center p-6 bg-gray-50 text-center">
        <p className="text-gray-600 mb-4">You must be signed in to view your profile.</p>
        <button
          onClick={onBackHome}
          className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition font-semibold"
        >
          Back to Home
        </button>
      </div>
    );
  }

  return (
<div className="min-h-screen bg-gray-50 flex flex-col items-center py-12 px-4">
  <div className="max-w-lg w-full bg-white p-8 rounded-2xl shadow-lg border border-gray-200">
    <h1 className="text-3xl font-bold text-gray-800 mb-8 text-center">Your Profile</h1>

    <div className="space-y-6">
      <div>
        <label htmlFor="displayName" className="block text-gray-700 font-semibold mb-2">
          Display Name
        </label>
        <input
          id="displayName"
          type="text"
          value={displayName}
          onChange={onDisplayNameChange}
          placeholder="Enter name to show on leaderboard"
          className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 transition"
        />
      </div>

      <div className="flex items-center space-x-3">
        <input
          id="leaderboardOptIn"
          type="checkbox"
          checked={leaderboardOptIn}
          onChange={onLeaderboardOptInChange}
          className="w-5 h-5 rounded cursor-pointer text-blue-600 focus:ring-2 focus:ring-blue-500"
        />
        <label htmlFor="leaderboardOptIn" className="cursor-pointer select-none font-semibold text-gray-700">
          Appear on leaderboard
        </label>
      </div>

      {saving && <p className="text-blue-600 font-medium">Saving...</p>}
      {!saving && saveMessage && <p className="text-green-600 font-medium">{saveMessage}</p>}

      <button
        onClick={onLogout}
        className="w-full mt-8 px-6 py-3 bg-red-500 text-white rounded-xl hover:bg-red-600 transition font-semibold shadow-md"
      >
        Sign Out
      </button>
    </div>
  </div>
</div>
  );
};

export default Profile;
