import React from 'react';

export default function Home({ onStartTutorial, onStartExperiment }) {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 px-6 py-12">
      {/* Hero Title */}
      <h1 className="text-5xl font-extrabold text-blue-700 text-center mb-6">
        Welcome to <span className="text-black">Read Racer</span>
      </h1>

      {/* Intro */}
      <p className="text-center max-w-2xl text-gray-700 mb-10 text-lg">
        Read Racer is a web-based experiment that explores how people comprehend
        text with and without the help of AI-generated summaries. Your speed and
        accuracy will help us uncover how technology impacts reading performance.
      </p>

      {/* Buttons */}
      <div className="flex flex-col items-center gap-4 mb-12">
        <button
          onClick={onStartExperiment}
          className="px-8 py-4 text-xl bg-green-600 text-white rounded-xl shadow hover:bg-green-700 transition"
        >
          üöÄ Start the Experiment
        </button>

        <button
          onClick={onStartTutorial}
          className="px-4 py-2 text-sm bg-blue-500 text-white rounded hover:bg-blue-600 transition"
        >
          üë®Try the Tutorial First
        </button>
      </div>

      {/* Stats Preview (placeholder) */}
      <div className="bg-white rounded-lg shadow-md px-6 py-4 w-full max-w-md text-center">
        <h3 className="text-lg font-semibold text-gray-800 mb-2">üìà Experiment Stats</h3>
        <p className="text-gray-600">1,000+ users tested ‚Ä¢ Avg reading time: 65s</p>
        <p className="text-gray-500 text-sm italic mt-1">Live tracking coming soon</p>
      </div>

      {/* Footer */}
      <footer className="mt-12 text-sm text-gray-400 text-center">
        Made with ‚ù§Ô∏è for a research project by Gal Raviv
      </footer>
    </div>
  );
}
