import React, { useState, useRef } from 'react';

const ExperimentReading = ({ text, onFinish }) => {
  const [started, setStarted] = useState(false);
  const [finished, setFinished] = useState(false);
  const startTimeRef = useRef(null);

  const handleStart = () => {
    setStarted(true);
    startTimeRef.current = Date.now();
  };

  const handleFinish = () => {
    if (!started) return;
    const endTime = Date.now();
    const elapsedSeconds = (endTime - startTimeRef.current) / 1000;
    setFinished(true);
    onFinish(elapsedSeconds);
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded shadow min-h-screen flex flex-col">
      <h2 className="text-3xl font-semibold mb-6 text-center">Experiment Reading</h2>

      {!started && (
        <button
          onClick={handleStart}
          className="mb-6 px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
        >
          Start Reading
        </button>
      )}

      {started && !finished && (
        <>
          <div
            className="mb-6 p-4 border border-gray-300 rounded overflow-y-auto max-h-[60vh] whitespace-pre-wrap"
            style={{ userSelect: 'text' }}
          >
            {text}
          </div>

          <button
            onClick={handleFinish}
            className="px-6 py-3 bg-green-600 text-white rounded hover:bg-green-700 transition"
          >
            Finished Reading
          </button>
        </>
      )}

      {finished && (
        <div className="text-center text-lg text-green-700 font-semibold">
          Reading time recorded. You may now proceed to the quiz.
        </div>
      )}
    </div>
  );
};

export default ExperimentReading;
