import React, { useState, useEffect } from 'react';

function TutorialReading({ text, onFinish }) {
  const [time, setTime] = useState(0);
  const [reading, setReading] = useState(true);

  useEffect(() => {
    let timer;
    if (reading) {
      timer = setInterval(() => {
        setTime((t) => t + 1);
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [reading]);

  const handleStop = () => {
    setReading(false);
  };

  const handleFinish = () => {
    setReading(false);
    onFinish(time);
  };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Tutorial Reading</h2>
      <div className="mb-4 whitespace-pre-wrap border p-4 rounded bg-gray-50">
        {text}
      </div>
      <p>Time reading: {time} seconds</p>
      {reading ? (
<button
  onClick={handleStop}
  className="mt-4 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
>
  Stop Reading
</button>

      ) : (
        <button
          onClick={handleFinish}
          className="mt-4 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
        >
          Finish Reading
        </button>
      )}
    </div>
  );
}

export default TutorialReading;
