import React from 'react';
import './LoadingScreen.css';

const LoadingScreen = () => {
  return (
    <div className="loading-container">
      <svg className="track" viewBox="0 0 1000 200">
        <path
          id="motionPath"
          d="M 50 150 Q 250 0 500 100 Q 750 200 950 50"
          fill="none"
          stroke="#e2e8f0"
          strokeWidth="4"
        />

        <g id="car">
          <g transform="scale(2)">
            <rect x="-10" y="-5" width="20" height="10" rx="2" fill="#1e3a8a" />
            <circle cx="-7" cy="5" r="2" fill="#333" />
            <circle cx="7" cy="5" r="2" fill="#333" />
          </g>
          <animateMotion dur="5s" repeatCount="indefinite" rotate="auto">
            <mpath href="#motionPath" />
          </animateMotion>
        </g>
      </svg>

      <div className="loading-text">Generating your reading content...</div>
    </div>
  );
};

export default LoadingScreen;
