import React, { useState, useEffect } from 'react';

function ExperimentReading({ text, onFinish, useSummary }) {
  const [time, setTime] = useState(0);
  const [isReading, setIsReading] = useState(true);

  useEffect(() => {
    if (!isReading) return;

    const timer = setInterval(() => setTime((t) => t + 1), 1000);
    return () => clearInterval(timer);
  }, [isReading]);

  const handleFinish = () => {
    setIsReading(false);
    onFinish(time);
  };

  return (
    <div className="max-w-3xl mx-auto p-6 flex flex-col space-y-6">
      <h2 className="text-2xl font-bold text-gray-800">
        {useSummary ? 'AI Summary Passage' : 'Full Passage'}
      </h2>

      <div className="bg-white p-6 rounded shadow whitespace-pre-wrap">
        {text}
      </div>

      <p className="text-gray-600">Time elapsed: {time} seconds</p>

      <button
        onClick={handleFinish}
        className="self-start px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Done Reading
      </button>
    </div>
  );
}

export default ExperimentReading;
