import React from 'react';

export default function TutorialResult({ readingTime, quizAnswers, onBackHome }) {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-6 max-w-md mx-auto text-center">
      <h2 className="text-3xl font-semibold mb-4">Tutorial Complete!</h2>

      <p className="mb-2 text-lg">Reading time: {readingTime} seconds</p>

      <p className="mb-2 text-lg">
        Your score: {quizAnswers.score} / {quizAnswers.total} (
        {(quizAnswers.score / quizAnswers.total) * 100}%)
      </p>

      <p
        className={`mb-6 font-semibold ${
          quizAnswers.passed ? 'text-green-600' : 'text-red-600'
        }`}
      >
        {quizAnswers.passed
          ? 'Nice job! You completed the tutorial.'
          : 'That’s okay — this was just practice. You’ll do better next time!'}
      </p>

      <button
        onClick={onBackHome}
        className="px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
      >
        Back to Home
      </button>
    </div>
  );
}
