import React, { useState } from 'react';
import Home from './Home';
import ReadingRoom from './ReadingRoom';
import Quiz from './Quiz';

const sampleText = `This is a sample AI-generated text for the reading experiment. 
Users will read this and then answer questions.`;

function App() {
  const [page, setPage] = useState('home');
  const [readingTime, setReadingTime] = useState(null);
  const [quizAnswers, setQuizAnswers] = useState(null);

  function startExperiment() {
    setPage('reading');
  }

  function finishReading(time) {
    setReadingTime(time);
    setPage('quiz');
  }

  function submitQuiz(answers) {
    setQuizAnswers(answers);
    setPage('finished');
  }

  return (
    <>
      {page === 'home' && <Home onStart={startExperiment} />}
      {page === 'reading' && <ReadingRoom text={sampleText} onFinish={finishReading} />}
      {page === 'quiz' && <Quiz onSubmit={submitQuiz} />}
      {page === 'finished' && (
        <div className="min-h-screen flex flex-col items-center justify-center p-6">
          <h2 className="text-2xl mb-4">You finished reading and quiz!</h2>
          <p className="mb-2">Reading time: {readingTime} seconds</p>
          <p className="mb-2">Your quiz answers: {JSON.stringify(quizAnswers)}</p>
          <button
            onClick={() => setPage('home')}
            className="px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
          >
            Back to Home
          </button>
        </div>
      )}
    </>
  );
}

export default App;
