import React, { useState, useEffect } from 'react';

function ExperimentReading({ text, onFinish, useSummary }) {
  const [time, setTime] = useState(0);
  const [isReading, setIsReading] = useState(true);

  useEffect(() => {
    if (!isReading) return;

    const timer = setInterval(() => setTime(t => t + 1), 1000);
    return () => clearInterval(timer);
  }, [isReading]);

  const handleFinish = () => {
    setIsReading(false);
    onFinish(time);
  };

  return (
    <div className="max-w-3xl mx-auto p-8 flex flex-col space-y-8 bg-gray-50 rounded-xl shadow-inner mt-8">
      <h2 className="text-3xl font-semibold text-gray-800">
        {useSummary ? 'AI Summary Passage' : 'Full Passage'}
      </h2>

      <div className="bg-white p-6 rounded-xl shadow-md border border-gray-200 whitespace-pre-wrap text-gray-800 leading-relaxed">
        {text}
      </div>

      <div className="flex items-center justify-between text-sm text-gray-600">
        <p>Time elapsed: <span className="font-medium">{time} seconds</span></p>
      </div>

      <button
        onClick={handleFinish}
        className="self-start px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition font-semibold shadow-sm"
      >
        Done Reading
      </button>
    </div>
  );
}

export default ExperimentReading;
