import React from 'react';

function Home({ onStartTutorial, onStartExperiment }) {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 px-4 py-12">
      <div className="max-w-xl w-full bg-white p-8 rounded-xl shadow-md text-center space-y-6">
        <h1 className="text-3xl font-bold text-gray-800">Welcome to the Reading Experiment</h1>
        <p className="text-gray-600">
          This study tests reading comprehension with and without AI summaries.
          Youâ€™ll start with a short tutorial, then complete a reading task and quiz.
        </p>

        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <button
            onClick={onStartTutorial}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
          >
            Start Tutorial
          </button>

          <button
            onClick={onStartExperiment}
            className="px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 transition"
          >
            Start Experiment
          </button>
        </div>
      </div>
    </div>
  );
}

export default Home;
