import React from 'react';

const ExperimentResult = ({ quizAnswers, readingTime, passageText, onBackHome }) => {
  if (!quizAnswers) {
    return (
      <div className="min-h-screen flex items-center justify-center p-6">
        <p className="text-gray-700">No quiz data found.</p>
      </div>
    );
  }

  const wordCount = passageText ? passageText.trim().split(/\s+/).length : 0;

  // Destructure the nested score objects from backend response
  const { total, mcq, saq_laq } = quizAnswers;

  // Defensive checks if data missing
  const totalScore = total?.score ?? 0;
  const totalPossible = total?.total ?? 0;

  // Calculate WPM (words per minute)
  const wpm = readingTime && wordCount ? Math.round((wordCount / readingTime) * 60) : 0;

  // Calculate points = (score / total) * WPM
  const points = totalPossible ? (totalScore / totalPossible) * wpm : 0;

  return (
    <div className="min-h-screen flex items-center justify-center px-4 py-10 bg-white">
      <div className="max-w-md w-full bg-gray-100 p-8 rounded-xl shadow text-center">
        <h2 className="text-3xl font-semibold mb-4 text-gray-800">Experiment Results</h2>

        <p className="text-lg mb-2">
          Multiple Choice Score: <span className="font-bold">{mcq?.score ?? 0}</span> /{' '}
          <span className="font-bold">{mcq?.total ?? 0}</span>
        </p>

        <p className="text-lg mb-2">
          Short/Long Answer Score: <span className="font-bold">{saq_laq?.score ?? 0}</span> /{' '}
          <span className="font-bold">{saq_laq?.total ?? 0}</span>
        </p>

        <p className="text-lg mb-2">
          Total Score: <span className="font-bold">{totalScore}</span> /{' '}
          <span className="font-bold">{totalPossible}</span>
        </p>

        <p className="text-lg mb-2">
          Reading Speed: <span className="font-mono font-semibold">{wpm} WPM</span>
        </p>

        <p className="text-lg mb-6">
          Total Points: <span className="font-bold text-blue-600">{points.toFixed(2)}</span>
        </p>

        <button
          onClick={onBackHome}
          className="w-full px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition"
        >
          Back to Home
        </button>
      </div>
    </div>
  );
};

export default ExperimentResult;
