import React, { useState } from 'react';

const questions = [
  {
    id: 'q1',
    type: 'multiple',
    question: 'What problem did the compass help solve for sailors?',
    choices: [
      'Cooking on ships',
      'Finding treasure',
      'Navigating without clear skies',
      'Communicating with land',
    ],
  },
  {
    id: 'q2',
    type: 'multiple',
    question: 'Where was the compass first invented?',
    choices: ['Egypt', 'Greece', 'China', 'India'],
  },
  {
    id: 'q3',
    type: 'short',
    question: 'Name one way people used to navigate before the invention of the compass.',
  },
];

function TutorialQuiz({ onSubmit }) {
  const [answers, setAnswers] = useState({});

  const handleChange = (id, value) => {
    setAnswers((prev) => ({ ...prev, [id]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(answers);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 px-4 py-10">
      <div className="bg-white shadow-xl rounded-2xl max-w-3xl w-full p-8 border border-gray-200">
        <h1 className="text-3xl font-bold mb-8 text-center text-gray-800">
          Tutorial Quiz
        </h1>
        <form onSubmit={handleSubmit} className="space-y-10">
          {questions.map(({ id, type, question, choices }) => (
            <div key={id} className="space-y-4">
              <p className="text-lg font-medium text-gray-800">{question}</p>

              {type === 'multiple' && (
                <div className="space-y-3">
                  {choices.map((choice, i) => (
                    <label
                      key={i}
                      className="flex items-center space-x-3 bg-gray-50 px-4 py-2 rounded-md hover:bg-gray-100 transition"
                    >
                      <input
                        type="radio"
                        name={id}
                        value={choice.toLowerCase()}
                        checked={answers[id] === choice.toLowerCase()}
                        onChange={() => handleChange(id, choice.toLowerCase())}
                        className="h-5 w-5 text-blue-600"
                        required
                      />
                      <span className="text-gray-700">{choice}</span>
                    </label>
                  ))}
                </div>
              )}

              {type === 'short' && (
                <textarea
                  className="w-full p-4 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  rows={4}
                  value={answers[id] || ''}
                  onChange={(e) => handleChange(id, e.target.value)}
                  placeholder="Type your answer here..."
                  required
                />
              )}
            </div>
          ))}

          <button
            type="submit"
            className="w-full py-3 bg-blue-600 hover:bg-blue-700 text-white font-semibold rounded-md shadow-md transition-colors"
          >
            Submit Answers
          </button>
        </form>
      </div>
    </div>
  );
}

export default TutorialQuiz;
