const ExperimentResult = ({ quizAnswers, readingTime, passageText, onBackHome }) => {
  if (!quizAnswers) {
    return (
      <div>No quiz data found.</div>
    );
  }

  const wordCount = passageText ? passageText.trim().split(/\s+/).length : 0;
  const { total, mcq, saq_laq } = quizAnswers;

  // readingTime in seconds, WPM calculation
  const wpm = readingTime && wordCount ? Math.round((wordCount / readingTime) * 60) : 0;
  // total points maybe based on total score and WPM
  const points = total && total.total ? (total.score / total.total) * wpm : 0;

  return (
    <div>
      <h2>Experiment Results</h2>
      <p>Multiple Choice: {mcq.score} / {mcq.total}</p>
      <p>Short/Long Answer: {saq_laq.score} / {saq_laq.total}</p>
      <p>Total Score: {total.score} / {total.total}</p>
      <p>Reading Speed: {wpm} WPM</p>
      <p>Total Points: {points.toFixed(2)}</p>
      <button onClick={onBackHome}>Back to Home</button>
    </div>
  );
};
