import React from 'react';

export default function TutorialResult({ readingTime, quizAnswers, onBackHome }) {
  if (!quizAnswers) {
    return <div>No quiz data found.</div>;
  }

  const correctAnswers = quizAnswers.filter(q => q.correct);
  const score = Math.round((correctAnswers.length / quizAnswers.length) * 100);

  return (
    <div className="text-center p-8">
      <h2 className="text-3xl font-bold mb-4">üéâ Tutorial Complete!</h2>
      <p className="text-lg mb-2">‚è±Ô∏è You finished reading in {readingTime} seconds.</p>
      <p className="text-lg mb-4">‚úÖ Your score: {score}%</p>
      <button
        onClick={onBackHome}
        className="mt-4 px-6 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
      >
        Back to Home
      </button>
    </div>
  );
}
