import React, { useState, useEffect, useRef } from 'react';

export default function ReadingRoom({ text, onFinish }) {
  const [startTime, setStartTime] = useState(null);
  const [readingTime, setReadingTime] = useState(0);
  const timerRef = useRef(null);

  useEffect(() => {
    setStartTime(Date.now());

    timerRef.current = setInterval(() => {
      setReadingTime(Math.floor((Date.now() - startTime) / 1000));
    }, 1000);

    return () => clearInterval(timerRef.current);
  }, [startTime]);

  return (
    <div className="min-h-screen bg-white p-8 flex flex-col max-w-3xl mx-auto">
      <h2 className="text-3xl font-semibold mb-4">Tutorial Reading:</h2>
      <div className="mb-8 p-6 border rounded-lg bg-gray-50 whitespace-pre-wrap">
        {text}
      </div>
      <div className="mb-4 text-gray-700">Reading time: {readingTime} seconds</div>
      <button
        onClick={() => {
          clearInterval(timerRef.current);
          onFinish(timeSpent);
        }}
        className="px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
      >
        Finished Reading
      </button>
    </div>
  );
}
