import React, { useState } from 'react';

const questions = [
  {
    id: 'q1',
    type: 'multiple',
    question: 'What problem did the compass help solve for sailors?',
    choices: [
      'Cooking on ships',
      'Finding treasure',
      'Navigating without clear skies',
      'Communicating with land',
    ],
  },
  {
    id: 'q2',
    type: 'multiple',
    question: 'Where was the compass first invented?',
    choices: ['Egypt', 'Greece', 'China', 'India'],
  },
  {
    id: 'q3',
    type: 'short',
    question: 'Name one way people used to navigate before the invention of the compass.',
  },
];

function TutorialQuiz({ onSubmit }) {
  const [answers, setAnswers] = useState({});

  const handleChange = (id, value) => {
    setAnswers((prev) => ({ ...prev, [id]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(answers);
  };

  return (
    <div className="max-w-3xl mx-auto p-6 flex flex-col space-y-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Tutorial Quiz</h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        {questions.map(({ id, type, question, choices }) => (
          <div key={id} className="bg-white p-6 rounded shadow">
            <p className="mb-2 font-semibold text-gray-700">{question}</p>

            {type === 'multiple' && choices && (
              <div className="space-y-2">
                {choices.map((choice, idx) => (
                  <label key={idx} className="flex items-center space-x-2 cursor-pointer">
                    <input
                      type="radio"
                      name={id}
                      value={choice}
                      checked={answers[id] === choice}
                      onChange={() => handleChange(id, choice)}
                      className="cursor-pointer"
                      required
                    />
                    <span>{choice}</span>
                  </label>
                ))}
              </div>
            )}

            {type === 'short' && (
              <textarea
                name={id}
                value={answers[id] || ''}
                onChange={(e) => handleChange(id, e.target.value)}
                rows={3}
                className="w-full border border-gray-300 rounded p-2 resize-none"
                placeholder="Type your answer here..."
                required
              />
            )}
          </div>
        ))}

        <button
          type="submit"
          className="self-start px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Submit Quiz
        </button>
      </form>
    </div>
  );
}

export default TutorialQuiz;
