import React from 'react';

const TutorialResult = ({ quizAnswers, readingTime, onBackHome }) => {
  if (!quizAnswers) {
    return (
      <div className="min-h-screen flex items-center justify-center p-6">
        <p className="text-gray-700">No quiz data found.</p>
      </div>
    );
  }
  function TutorialResult({ readingTime, quizAnswers, onBackHome }) {
  // tutorialText should be imported or passed in, or hardcoded here if small
  const tutorialText = `The History of the Compass

  The compass is one of the most important inventions in human history. It allowed sailors to travel across oceans without getting lost. Before the compass, people used the sun and stars to find their way. This worked well on clear days and nights, but it was hard when the sky was cloudy.

  The first compasses were invented in China around 2,000 years ago. They used a spoon-shaped piece of magnetic metal that pointed south. Later, people created compasses with a magnetic needle that floated in water or was placed on a pin. These versions were more accurate and easier to use.

  By the 12th century, the compass spread to Europe and the Middle East. It helped explorers like Christopher Columbus and Vasco da Gama sail across unknown seas. With the compass, people could explore, trade, and connect with others around the world.

  Today, we have GPS, but the compass is still used in hiking, boating, and emergencies. It’s a simple tool that changed the world by helping people travel farther and safer.`;

  // count words
  const wordCount = tutorialText.trim().split(/\s+/).length;

  // Calculate WPM
  const wpm = readingTime ? Math.round((wordCount / readingTime) * 60) : 0;

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded shadow text-center">
      <h2 className="text-2xl font-bold mb-4">Tutorial Results</h2>
      <p className="mb-2">
        Reading speed: <strong>{wpm}</strong> words per minute
      </p>
      <p className="mb-4">
        Quiz score: <strong>{quizAnswers?.score ?? 0} / {quizAnswers?.total ?? 3}</strong>
      </p>
      <button
        className="px-5 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        onClick={onBackHome}
      >
        Back to Home
      </button>
    </div>
  );
}

  const { score, total, passed } = quizAnswers;

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-6 py-12 bg-white">
      <div className="max-w-md w-full bg-gray-100 p-8 rounded-xl shadow text-center">
        <h2 className="text-3xl font-semibold mb-4 text-gray-800">Tutorial Results</h2>
        <p className="text-lg mb-2">
          You scored <span className="font-bold">{score}</span> out of <span className="font-bold">{total}</span>.
        </p>
        <p className={`mb-6 text-xl font-semibold ${passed ? 'text-green-600' : 'text-red-600'}`}>
          {passed ? 'Great job! You passed the tutorial.' : 'Keep practicing!'}
        </p>

        {readingTime && (
          <p className="mb-6 text-gray-700">
            Reading Time: <span className="font-mono">{readingTime} seconds</span>
          </p>
        )}

        <button
          onClick={onBackHome}
          className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition"
        >
          Back to Home
        </button>
      </div>
    </div>
  );
};

export default TutorialResult;
