import React, { useState } from 'react';
import Home from './components/Home';
import TutorialReading from './tutorial/TutorialReading';
import TutorialQuiz from './tutorial/TutorialQuiz';
import TutorialResult from './tutorial/TutorialResult';
import ExperimentSummary from './experiment/ExperimentSummary';
import ExperimentFullText from './experiment/ExperimentFullText';
import ExperimentQuiz from './experiment/ExperimentQuiz';
import ExperimentResult from './experiment/ExperimentResult';

const tutorialText = `The History of the Compass

The compass is one of the most important inventions in human history. It allowed sailors to travel across oceans without getting lost. Before the compass, people used the sun and stars to find their way. This worked well on clear days and nights, but it was hard when the sky was cloudy.

The first compasses were invented in China around 2,000 years ago. They used a spoon-shaped piece of magnetic metal that pointed south. Later, people created compasses with a magnetic needle that floated in water or was placed on a pin. These versions were more accurate and easier to use.

By the 12th century, the compass spread to Europe and the Middle East. It helped explorers like Christopher Columbus and Vasco da Gama sail across unknown seas. With the compass, people could explore, trade, and connect with others around the world.

Today, we have GPS, but the compass is still used in hiking, boating, and emergencies. Itâ€™s a simple tool that changed the world by helping people travel farther and safer.`;

const correctAnswers = {
  q1: 'c',
  q2: 'b',
  q3Keywords: ['sun', 'star', 'skies', 'sky', 'suns', 'stars'],
};

function checkAnswers(userAnswers) {
  let score = 0;
  let total = 3;

  if (userAnswers.q1 === correctAnswers.q1) score++;
  if (userAnswers.q2 === correctAnswers.q2) score++;

  const answerText = userAnswers.q3.toLowerCase();
  let keywordMatches = 0;
  correctAnswers.q3Keywords.forEach((keyword) => {
    if (answerText.includes(keyword)) keywordMatches++;
  });

  if (keywordMatches >= correctAnswers.q3Keywords.length / 2) score++;

  return { score, total, passed: score / total >= 0.8 };
}

function App() {
  const [page, setPage] = useState('home'); // 'home' | 'reading' | 'quiz' | 'finished'
  const [mode, setMode] = useState(null); // 'tutorial' | 'experiment'
  const [readingTime, setReadingTime] = useState(null);
  const [quizAnswers, setQuizAnswers] = useState(null);

  const startTutorial = () => {
    setMode('tutorial');
    setPage('reading');
  };

  const finishReadingTutorial = () => {
    setReadingTime(time);
    setPage('quiz');
  };

  const startExperiment = () => {
    setMode('experiment');
    setPage('reading');
  };

  const finishReadingExperiment = async (time) => {
    setReadingTime(time);
    setPage('loading');

    try {
      // ðŸ” Later, you'll replace this with a real API call
      const res = await fetch('/api/generate-questions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: sampleText }), // â† real AI text
      });

      const data = await res.json();
      setExperimentQuestions(data.questions); // must be an array
      setPage('quiz');
    } catch (err) {
      console.error('Failed to get questions from AI:', err);
      alert('Failed to load questions. Please try again.');
      setPage('home');
    }
  };


  const submitQuiz = (answers) => {
    const result = checkAnswers(answers);
    setQuizAnswers({ ...answers, ...result });
    setPage('finished');
  };

  const backToHome = () => {
    setPage('home');
    setMode(null);
    setReadingTime(null);
    setQuizAnswers(null);
  };
  const sampleText = `This is a long AI-generated article that the user would read during the experiment. It has several ideas and paragraphs designed to simulate real reading.`;
  const sampleSummary = `This is a brief AI-generated summary of the longer article above.`;

  const showSummary = useState(() => Math.random() < 0.5)[0];

  const [experimentQuestions, setExperimentQuestions] = useState([]);

  return (
    <>
      {page === 'home' && (
        <Home
          onStartTutorial={startTutorial}
          onStartExperiment={startExperiment}
        />
      )}

      {/* TUTORIAL FLOW */}
      {mode === 'tutorial' && page === 'reading' && (
        <TutorialReading text={tutorialText} onFinish={finishReadingTutorial} />
      )}
      {mode === 'tutorial' && page === 'quiz' && (
        <TutorialQuiz onSubmit={submitQuiz} />
      )}
      {mode === 'tutorial' && page === 'finished' && (
        <TutorialResult
          readingTime={readingTime}
          quizAnswers={quizAnswers}
          onBackHome={backToHome}
        />
      )}
      {/* EXPERIMENT FLOW */}
      {mode === 'experiment' && page === 'reading' && (
        showSummary ? (
          <ExperimentSummary summary={sampleSummary} onFinish={finishReadingExperiment} />
        ) : (
          <ExperimentFullText text={sampleText} onFinish={finishReadingExperiment} />
        )
      )}
      {page === 'loading' && (
      <div className="min-h-screen flex flex-col items-center justify-center p-6">
        <p className="text-xl text-gray-700">Generating your questions...</p>
      </div>
      )}

      {mode === 'experiment' && page === 'quiz' && (
        <ExperimentQuiz
          questions={experimentQuestions}
          onSubmit={(answers) => {
            const result = checkAnswers(answers); // you can still simulate this for now
            setQuizAnswers({ ...answers, ...result });
            setPage('finished');
          }}
        />
      )}

      {mode === 'experiment' && page === 'finished' && (
        <ExperimentResult
          readingTime={readingTime}
          quizAnswers={quizAnswers}
          onBackHome={backToHome}
        />
      )}
    </>
  );
}

export default App;
