import React, { useState, useEffect } from "react";

const Profile = ({ user, setUser, onLogout, onBackHome }) => {
  // Mocked stats; replace with real data fetching later
  const [stats, setStats] = useState({
    totalExperiments: 12,
    summaryExperiments: 7,
    fullPassageExperiments: 5,
    dateJoined: user?.dateJoined || "2024-01-15",
  });

  // Dark mode preference
  const [darkMode, setDarkMode] = useState(
    () => localStorage.getItem("darkMode") === "true"
  );

  // Toggle dark mode & persist in localStorage
  const toggleDarkMode = () => {
    setDarkMode((prev) => {
      localStorage.setItem("darkMode", !prev);
      return !prev;
    });
  };

  // Apply dark mode class on body
  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
    }
  }, [darkMode]);

  // Editable display name
  const [displayName, setDisplayName] = useState(user?.displayName || "");

  const handleDisplayNameChange = (e) => setDisplayName(e.target.value);

  const saveDisplayName = () => {
    const updatedUser = { ...user, displayName };
    setUser(updatedUser);
    localStorage.setItem("user", JSON.stringify(updatedUser));
    alert("Display name saved!");
  };

  return (
    <div className={`min-h-screen p-6 max-w-xl mx-auto ${darkMode ? "bg-gray-900 text-gray-100" : "bg-gray-50 text-gray-900"} rounded-xl`}>
      <h1 className="text-4xl font-bold mb-6 text-center">Profile</h1>

      <section className="mb-8 p-6 bg-white dark:bg-gray-800 rounded-xl shadow-md">
        <h2 className="text-2xl font-semibold mb-4">User Info</h2>
        <label className="block mb-2 font-medium">Display Name:</label>
        <input
          type="text"
          value={displayName}
          onChange={handleDisplayNameChange}
          className="w-full px-3 py-2 border rounded-lg dark:bg-gray-700 dark:border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Your display name"
        />
        <button
          onClick={saveDisplayName}
          className="mt-3 px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          Save
        </button>

        <p className="mt-4 text-sm text-gray-600 dark:text-gray-400">
          Email: <span className="font-medium">{user?.email || "N/A"}</span>
        </p>
        <p className="text-sm text-gray-600 dark:text-gray-400 mt-1">
          User ID: <span className="font-mono">{user?.uuid}</span>
        </p>
      </section>

      <section className="mb-8 p-6 bg-white dark:bg-gray-800 rounded-xl shadow-md">
        <h2 className="text-2xl font-semibold mb-4">Your Statistics</h2>
        <ul className="space-y-2">
          <li>Total Experiments Taken: <span className="font-semibold">{stats.totalExperiments}</span></li>
          <li>Summary Experiments: <span className="font-semibold">{stats.summaryExperiments}</span></li>
          <li>Full Passage Experiments: <span className="font-semibold">{stats.fullPassageExperiments}</span></li>
          <li>Date Joined: <span className="font-semibold">{new Date(stats.dateJoined).toLocaleDateString()}</span></li>
        </ul>
      </section>

      <section className="mb-8 p-6 bg-white dark:bg-gray-800 rounded-xl shadow-md">
        <h2 className="text-2xl font-semibold mb-4">Preferences</h2>
        <div className="flex items-center gap-4">
          <label htmlFor="darkModeToggle" className="font-medium">Dark Mode</label>
          <input
            type="checkbox"
            id="darkModeToggle"
            checked={darkMode}
            onChange={toggleDarkMode}
            className="w-5 h-5 cursor-pointer rounded border-gray-300 dark:border-gray-600 text-blue-600 focus:ring-blue-500"
          />
        </div>
      </section>

      <section className="mb-8 p-6 bg-white dark:bg-gray-800 rounded-xl shadow-md">
        <h2 className="text-2xl font-semibold mb-4">Legal</h2>
        <ul className="space-y-2 text-blue-600 underline cursor-pointer">
          <li onClick={() => window.open("/terms-of-service", "_blank")}>Terms of Service</li>
          <li onClick={() => window.open("/privacy-policy", "_blank")}>Privacy Policy</li>
        </ul>
      </section>

      <div className="flex justify-between gap-4">
        <button
          onClick={onLogout}
          className="flex-1 py-3 bg-red-600 text-white rounded-lg hover:bg-red-700 transition font-semibold"
        >
          Log Out
        </button>

        <button
          onClick={onBackHome}
          className="flex-1 py-3 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition font-semibold"
        >
          Back to Home
        </button>
      </div>
    </div>
  );
};

export default Profile;
