import React, { useEffect, useState } from 'react';

export default function ExperimentFullText({ text, onFinish }) {
  const [startTime, setStartTime] = useState(null);

  useEffect(() => {
    setStartTime(Date.now());
  }, []);

  const handleFinish = () => {
    const endTime = Date.now();
    const seconds = Math.round((endTime - startTime) / 1000);
    onFinish(seconds);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center p-6 max-w-2xl mx-auto">
      <h2 className="text-3xl font-semibold mb-4">Full Text Reading</h2>
      <p className="mb-6 text-gray-700 whitespace-pre-line">{text}</p>
      <button
        onClick={handleFinish}
        className="px-6 py-3 bg-blue-600 text-white rounded hover:bg-blue-700 transition"
      >
        I'm Done Reading
      </button>
    </div>
  );
}
