import React, { useState } from 'react';

export default function Quiz({ onSubmit }) {
  const [answers, setAnswers] = useState({
    q1: '',
    q2: '',
    q3: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAnswers(prev => ({ ...prev, [name]: value }));
  };

  const allAnswered = answers.q1 && answers.q2 && answers.q3.trim() !== '';

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-50 p-6 max-w-3xl mx-auto">
      <h2 className="text-3xl font-semibold mb-6">Tutorial Quiz</h2>
      <p className="mb-4 text-gray-700">Now try answering some practice questions based on what you just read:</p>

      <form className="w-full max-w-md space-y-6" onSubmit={e => e.preventDefault()}>
        {/* Question 1 */}
        <div>
          <label className="block font-medium mb-1">
            1. What problem did the compass help solve for sailors?
          </label>
          <select
            name="q1"
            value={answers.q1}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          >
            <option value="">-- Select an answer --</option>
            <option value="a">Cooking on ships</option>
            <option value="b">Finding treasure</option>
            <option value="c">Navigating without clear skies</option>
            <option value="d">Communicating with land</option>
          </select>
        </div>

        {/* Question 2 */}
        <div>
          <label className="block font-medium mb-1">
            2. Where was the compass first invented?
          </label>
          <select
            name="q2"
            value={answers.q2}
            onChange={handleChange}
            className="w-full border rounded px-3 py-2"
          >
            <option value="">-- Select an answer --</option>
            <option value="a">Egypt</option>
            <option value="b">Users of the website</option>
            <option value="c">Teachers and staff</option>
            <option value="d">Teachers and staff</option>
          </select>
        </div>

        {/* Question 3 */}
        <div>
          <label className="block font-medium mb-1">
            3. Briefly describe what users are expected to do.
          </label>
          <textarea
            name="q3"
            value={answers.q3}
            onChange={handleChange}
            rows={3}
            className="w-full border rounded px-3 py-2"
          />
        </div>

        <button
          type="button"
          disabled={!allAnswered}
          onClick={() => onSubmit(answers)}
          className={`w-full py-3 rounded text-white ${allAnswered ? 'bg-blue-600 hover:bg-blue-700' : 'bg-gray-400 cursor-not-allowed'}`}
        >
          Submit Tutorial Quiz
        </button>
      </form>
    </div>
  );
}
