import React, { useEffect, useState } from "react";
import {
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

// Custom Tooltip to show only key info
const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length > 0) {
    const data = payload[0].payload;
    return (
      <div className="bg-white p-2 rounded shadow border text-sm">
        <p>
          <strong>Reading Speed:</strong>{" "}
          {Math.round(data.value ?? data.readingSpeed)} WPM
        </p>
        <p>
          <strong>Accuracy:</strong> {(data.accuracy * 100).toFixed(1)}%
        </p>
        {"points" in data && (
          <p>
            <strong>Points:</strong> {data.points.toFixed(2)}
          </p>
        )}
      </div>
    );
  }
  return null;
};

const GlobalStats = () => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // "effective" or "real"
  const [mode, setMode] = useState("effective");

  useEffect(() => {
    fetch("https://read-racer.azurewebsites.net/api/getGlobalStats")
      .then((res) => res.json())
      .then((json) => {
        // NO filtering here, just use raw data
        setData({
          summary: json.summaryAttempts || [],
          passage: json.passageAttempts || [],
          summaryAvg: json.summaryAvg,
          passageAvg: json.passageAvg,
          leaderboardSummary: json.leaderboardSummary || [],
          leaderboardPassage: json.leaderboardPassage || [],
        });
        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to fetch global stats:", err);
        setError("Failed to load data");
        setLoading(false);
      });
  }, []);

  if (loading)
    return <div className="text-center p-6">Loading global stats...</div>;
  if (error)
    return <div className="text-center p-6 text-red-500">{error}</div>;

  // Prepare data for the scatter plot depending on mode
  const summaryData = data.summary.map((a) => ({
    value:
      mode === "effective"
        ? a.effectiveWPM ?? a.realWPM ?? a.readingSpeed
        : a.realWPM ?? a.effectiveWPM ?? a.readingSpeed,
    accuracy: a.accuracy,
  }));

  // Full passage always uses realWPM or readingSpeed
  const passageData = data.passage.map((a) => ({
    value: a.realWPM ?? a.readingSpeed,
    accuracy: a.accuracy,
  }));

  // Averages for stars
  const summaryAvgValue =
    mode === "effective"
      ? data.summaryAvg.effectiveWPM ?? data.summaryAvg.realWPM ?? data.summaryAvg.readingSpeed
      : data.summaryAvg.realWPM ?? data.summaryAvg.effectiveWPM ?? data.summaryAvg.readingSpeed;

  const passageAvgValue = data.passageAvg.realWPM ?? data.passageAvg.readingSpeed;

  return (
    <div className="p-6 max-w-4xl mx-auto space-y-8">
      <h2 className="text-3xl font-bold text-center">Global Experiment Stats</h2>

      {/* Mode selector */}
      <div className="mb-4 text-center">
        <label className="mr-2 font-semibold">Select Graph Mode:</label>
        <select
          value={mode}
          onChange={(e) => setMode(e.target.value)}
          className="border rounded p-1"
        >
          <option value="effective">Effective WPM (AI Summaries)</option>
          <option value="real">Real WPM (AI Summaries)</option>
        </select>
      </div>

      {/* Combined Scatter Chart */}
      <div>
        <h3 className="text-xl font-semibold mb-2 text-indigo-700">
          {mode === "effective"
            ? "Effective WPM (AI Summaries) vs Reading Speed (Full Passage)"
            : "Real WPM (AI Summaries) vs Reading Speed (Full Passage)"}
        </h3>
        <ResponsiveContainer width="100%" height={350}>
          <ScatterChart margin={{ top: 10, right: 20, bottom: 20, left: 0 }}>
            <CartesianGrid />
            <XAxis
              type="number"
              dataKey="value"
              name="Reading Speed (WPM)"
              unit="WPM"
              domain={["auto", "auto"]}
            />
            <YAxis
              type="number"
              dataKey="accuracy"
              name="Accuracy"
              unit="%"
              domain={[0, 1]}
              tickFormatter={(tick) => `${(tick * 100).toFixed(0)}%`}
            />
            <Tooltip content={<CustomTooltip />} cursor={{ strokeDasharray: "3 3" }} />
            <Legend
              formatter={(value) => {
                if (value === "AI Summaries") return "AI Summaries (Participants)";
                if (value === "Full Passage") return "Full Passage (Participants)";
                if (value === "Avg AI Summaries") return "Average AI Summaries";
                if (value === "Avg Full Passage") return "Average Full Passage";
                return value;
              }}
            />
            <Scatter
              name="AI Summaries"
              data={summaryData}
              fill="#3b82f6"
              shape="circle"
            />
            <Scatter
              name="Full Passage"
              data={passageData}
              fill="#10b981"
              shape="circle"
            />
            {/* Average points */}
            {data.summaryAvg && (
              <Scatter
                name="Avg AI Summaries"
                data={[{ value: summaryAvgValue, accuracy: data.summaryAvg.accuracy }]}
                fill="#ef4444"
                shape="star"
              />
            )}
            {data.passageAvg && (
              <Scatter
                name="Avg Full Passage"
                data={[{ value: passageAvgValue, accuracy: data.passageAvg.accuracy }]}
                fill="#fbbf24"
                shape="star"
              />
            )}
          </ScatterChart>
        </ResponsiveContainer>
      </div>

      {/* Two leaderboards */}
      <div>
        <h3 className="text-xl font-semibold mb-3">Leaderboard — AI Summaries (Top 10 by Points)</h3>
        <table className="w-full border rounded-xl overflow-hidden text-left mb-8">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-2 px-4">Rank</th>
              <th className="py-2 px-4">User</th>
              <th className="py-2 px-4">Points</th>
              <th className="py-2 px-4">Speed (WPM)</th>
              <th className="py-2 px-4">Accuracy</th>
            </tr>
          </thead>
          <tbody>
            {data.leaderboardSummary.slice(0, 10).map((entry, index) => (
              <tr key={index} className="border-t">
                <td className="py-2 px-4 font-bold">#{index + 1}</td>
                <td className="py-2 px-4 font-mono text-sm">{entry.userName || "Anonymous"}</td>
                <td className="py-2 px-4">{entry.points.toFixed(2)}</td>
                <td className="py-2 px-4">{Math.round(entry.readingSpeed ?? entry.realWPM ?? 0)}</td>
                <td className="py-2 px-4">{(entry.accuracy * 100).toFixed(1)}%</td>
              </tr>
            ))}
          </tbody>
        </table>

        <h3 className="text-xl font-semibold mb-3">Leaderboard — Full Passages (Top 10 by Points)</h3>
        <table className="w-full border rounded-xl overflow-hidden text-left">
          <thead className="bg-gray-100">
            <tr>
              <th className="py-2 px-4">Rank</th>
              <th className="py-2 px-4">User</th>
              <th className="py-2 px-4">Points</th>
              <th className="py-2 px-4">Speed (WPM)</th>
              <th className="py-2 px-4">Accuracy</th>
            </tr>
          </thead>
          <tbody>
            {data.leaderboardPassage.slice(0, 10).map((entry, index) => (
              <tr key={index} className="border-t">
                <td className="py-2 px-4 font-bold">#{index + 1}</td>
                <td className="py-2 px-4 font-mono text-sm">{entry.userName || "Anonymous"}</td>
                <td className="py-2 px-4">{entry.points.toFixed(2)}</td>
                <td className="py-2 px-4">{Math.round(entry.readingSpeed ?? entry.realWPM ?? 0)}</td>
                <td className="py-2 px-4">{(entry.accuracy * 100).toFixed(1)}%</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default GlobalStats;
