import React, { useEffect, useState } from "react";
import {
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

// Custom Tooltip to show key info only
const CustomTooltip = ({ active, payload }) => {
  if (active && payload && payload.length > 0) {
    return (
      <div className="bg-white p-2 rounded shadow border text-sm">
        {payload.map((entry, index) => {
          const data = entry.payload ?? {};
          const isGlobal = entry.name?.toLowerCase().includes("avg");

          return (
            <div key={index} className="mb-2 border-b last:border-b-0 pb-1">
              {isGlobal && (
                <p className="text-xs text-red-600 font-semibold mb-1">
                  Global Average
                </p>
              )}
              <p>
                <strong>Reading Speed:</strong>{" "}
                {Math.round(data.value ?? data.readingSpeed ?? 0)} WPM
              </p>
              <p>
                <strong>Accuracy:</strong>{" "}
                {((data.accuracy ?? 0) * 100).toFixed(1)}%
              </p>
              {typeof data.points === "number" && (
                <p>
                  <strong>Points:</strong> {data.points.toFixed(2)}
                </p>
              )}
            </div>
          );
        })}
      </div>
    );
  }
  return null;
};

const PersonalResults = ({ userId }) => {
  const [summaryAttempts, setSummaryAttempts] = useState([]);
  const [fullPassageAttempts, setFullPassageAttempts] = useState([]);
  const [globalAverages, setGlobalAverages] = useState({ summary: null, full: null });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const [mode, setMode] = useState("effective");

  // New checkbox states for toggling personal attempts and global averages
  const [showPersonal, setShowPersonal] = useState(true);
  const [showGlobal, setShowGlobal] = useState(false);

  useEffect(() => {
    if (!userId) return;

    async function fetchData() {
      setLoading(true);
      setError(null);

      try {
        const res = await fetch(
          `https://read-racer.azurewebsites.net/api/getUserResults?userId=${encodeURIComponent(userId)}`
        );
        if (!res.ok) throw new Error(`HTTP error! status: ${res.status}`);
        const data = await res.json();

        const results = Array.isArray(data) ? data : data.results || [];
        const summary = results.filter(
          (r) => r.useSummary === true || r.useSummary === "true" || r.useSummary === "True"
        );
        const full = results.filter(
          (r) => !(r.useSummary === true || r.useSummary === "true" || r.useSummary === "True")
        );

        setSummaryAttempts(summary);
        setFullPassageAttempts(full);

        setGlobalAverages({
          summary: data.summaryAvg || null,
          full: data.passageAvg || null,
        });
      } catch (err) {
        console.error("Failed to load user results:", err);
        setError(err.message || "Failed to load user results");
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [userId]);

  const renderCombinedChart = () => {
    if ((!summaryAttempts.length && !fullPassageAttempts.length && !showGlobal) || !globalAverages) {
      return <p>No attempts yet.</p>;
    }

    const summaryData = summaryAttempts.map((a, index) => ({
      id: index,
      value:
        mode === "effective"
          ? a.effectiveWPM ?? a.realWPM ?? a.readingSpeed ?? 0
          : a.realWPM ?? a.effectiveWPM ?? a.readingSpeed ?? 0,
      accuracy: a.total > 0 ? a.score / a.total : 0,
      points: a.points ?? null,
    }));

    const passageData = fullPassageAttempts.map((a, index) => ({
      id: index + 10000,
      value: a.realWPM ?? a.readingSpeed ?? 0,
      accuracy: a.total > 0 ? a.score / a.total : 0,
      points: a.points ?? null,
    }));

    const summaryAvgValue =
      mode === "effective"
        ? globalAverages.summary?.effectiveWPM ?? globalAverages.summary?.realWPM ?? globalAverages.summary?.readingSpeed ?? 0
        : globalAverages.summary?.realWPM ?? globalAverages.summary?.effectiveWPM ?? globalAverages.summary?.readingSpeed ?? 0;

    const passageAvgValue =
      globalAverages.full?.realWPM ?? globalAverages.full?.readingSpeed ?? 0;

    return (
      <div className="mb-8">
        <h3 className="text-xl font-semibold mb-2 text-gray-700">
          {mode === "effective"
            ? "Effective WPM (AI Summaries) vs Reading Speed (Full Passage)"
            : "Real WPM (AI Summaries) vs Reading Speed (Full Passage)"}
        </h3>

        {/* Checkboxes for toggling personal attempts and global averages */}
        <div className="mb-4 flex justify-center items-center gap-6">
          <label className="inline-flex items-center space-x-2">
            <input
              type="checkbox"
              checked={showPersonal}
              onChange={() => setShowPersonal((prev) => !prev)}
              className="form-checkbox"
            />
            <span>Show Personal Attempts</span>
          </label>

          <label className="inline-flex items-center space-x-2">
            <input
              type="checkbox"
              checked={showGlobal}
              onChange={() => setShowGlobal((prev) => !prev)}
              className="form-checkbox"
            />
            <span>Show Global Averages</span>
          </label>

          <label className="inline-flex items-center space-x-2 ml-auto">
            <span className="font-semibold">Select Graph Mode:</span>
            <select
              value={mode}
              onChange={(e) => setMode(e.target.value)}
              className="border rounded p-1"
            >
              <option value="effective">Effective WPM (AI Summaries)</option>
              <option value="real">Real WPM (AI Summaries)</option>
            </select>
          </label>
        </div>

        <ResponsiveContainer width="100%" height={350}>
          <ScatterChart margin={{ top: 10, right: 20, bottom: 20, left: 0 }}>
            <CartesianGrid />
            <XAxis
              type="number"
              dataKey="value"
              name="Reading Speed (WPM)"
              unit=" WPM"
              domain={["auto", "auto"]}
            />
            <YAxis
              type="number"
              dataKey="accuracy"
              name="Accuracy"
              domain={[0, 1]}
              tickFormatter={(tick) => `${(tick * 100).toFixed(0)}%`}
            />
            <Tooltip content={<CustomTooltip />} cursor={{ strokeDasharray: "3 3" }} />
            <Legend
              formatter={(value) => {
                if (value === "AI Summaries") return "AI Summaries (Attempts)";
                if (value === "Full Passage") return "Full Passage (Attempts)";
                if (value === "Avg AI Summaries") return "Average AI Summaries";
                if (value === "Avg Full Passage") return "Average Full Passage";
                return value;
              }}
            />
            {showPersonal && (
              <>
                <Scatter
                  name="AI Summaries"
                  data={summaryData}
                  fill="#3b82f6"
                  shape="circle"
                  key="summary"
                  dataKey={{ x: "value", y: "accuracy" }}
                />
                <Scatter
                  name="Full Passage"
                  data={passageData}
                  fill="#10b981"
                  shape="circle"
                  key="passage"
                  dataKey={{ x: "value", y: "accuracy" }}
                />
              </>
            )}
            {showGlobal && globalAverages.summary && (
              <Scatter
                name="Avg AI Summaries"
                data={[{ value: summaryAvgValue, accuracy: globalAverages.summary.accuracy ?? 0 }]}
                fill="#ef4444"
                shape="star"
                key="avg-summary"
                dataKey={{ x: "value", y: "accuracy" }}
              />
            )}
            {showGlobal && globalAverages.full && (
              <Scatter
                name="Avg Full Passage"
                data={[{ value: passageAvgValue, accuracy: globalAverages.full.accuracy ?? 0 }]}
                fill="#fbbf24"
                shape="star"
                key="avg-passage"
                dataKey={{ x: "value", y: "accuracy" }}
              />
            )}
          </ScatterChart>
        </ResponsiveContainer>
      </div>
    );
  };

  if (!userId) return <p>Please log in to see your personal results.</p>;
  if (loading) return <p>Loading your results...</p>;
  if (error) return <p className="text-red-600 font-semibold">Error: {error}</p>;

  return (
    <div className="p-6 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6 text-gray-800">Your Personal Results</h2>
      {renderCombinedChart()}
    </div>
  );
};

export default PersonalResults;
