import React from 'react';

const tutorialText = `The History of the Compass

The compass is one of the most important inventions in human history. It allowed sailors to travel across oceans without getting lost. Before the compass, people used the sun and stars to find their way. This worked well on clear days and nights, but it was hard when the sky was cloudy.

The first compasses were invented in China around 2,000 years ago. They used a spoon-shaped piece of magnetic metal that pointed south. Later, people created compasses with a magnetic needle that floated in water or was placed on a pin. These versions were more accurate and easier to use.

By the 12th century, the compass spread to Europe and the Middle East. It helped explorers like Christopher Columbus and Vasco da Gama sail across unknown seas. With the compass, people could explore, trade, and connect with others around the world.

Today, we have GPS, but the compass is still used in hiking, boating, and emergencies. It’s a simple tool that changed the world by helping people travel farther and safer.`;

const TutorialResult = ({ quizAnswers, readingTime, onBackHome }) => {
  if (!quizAnswers) {
    return (
      <div className="min-h-screen flex items-center justify-center p-6 bg-gray-50">
        <p className="text-gray-600 text-lg">No quiz data found.</p>
      </div>
    );
  }

  const wordCount = tutorialText.trim().split(/\s+/).length;
  const { score, total } = quizAnswers;

  const wpm = readingTime ? Math.round((wordCount / readingTime) * 60) : 0;
  const points = total ? (score / total) * wpm : 0;

  return (
    <div className="min-h-screen flex items-center justify-center px-4 py-10 bg-gray-50">
      <div className="max-w-md w-full bg-white p-8 rounded-2xl shadow-lg border border-gray-200 text-center">
        <h2 className="text-3xl font-bold text-gray-800 mb-6">Your Tutorial Results</h2>

        <div className="space-y-3 text-gray-700 text-md">
          <div className="flex justify-between">
            <span>Quiz Score:</span>
            <span className="font-semibold">{score} / {total}</span>
          </div>

          <div className="flex justify-between">
            <span>Reading Speed:</span>
            <span className="font-mono font-semibold">{wpm} WPM</span>
          </div>

          <div className="flex justify-between text-lg font-medium text-blue-700 mt-4 border-t pt-4">
            <span>Total Points:</span>
            <span>{points.toFixed(2)}</span>
          </div>
        </div>

        <button
          onClick={user ? onStartExperiment : undefined}
            disabled={!user}
                      className="mt-8 w-full px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition font-semibold shadow-sm"
        >
          Start Experiment
        </button>
      </div>
    </div>
  );
};

export default TutorialResult;
