import React, { useState } from 'react';
import Home from './components/Home';
import TutorialReading from './tutorial/TutorialReading';
import TutorialQuiz from './tutorial/TutorialQuiz';
import TutorialResult from './tutorial/TutorialResult';
import ExperimentFullText from './experiment/ExperimentFullText';
import ExperimentSummary from './experiment/ExperimentSummary';
import ExperimentQuiz from './experiment/ExperimentQuiz';
import ExperimentResult from './experiment/ExperimentResult';

const tutorialText = `The History of the Compass

The compass is one of the most important inventions in human history. It allowed sailors to travel across oceans without getting lost. Before the compass, people used the sun and stars to find their way. This worked well on clear days and nights, but it was hard when the sky was cloudy.

The first compasses were invented in China around 2,000 years ago. They used a spoon-shaped piece of magnetic metal that pointed south. Later, people created compasses with a magnetic needle that floated in water or was placed on a pin. These versions were more accurate and easier to use.

By the 12th century, the compass spread to Europe and the Middle East. It helped explorers like Christopher Columbus and Vasco da Gama sail across unknown seas. With the compass, people could explore, trade, and connect with others around the world.

Today, we have GPS, but the compass is still used in hiking, boating, and emergencies. Itâ€™s a simple tool that changed the world by helping people travel farther and safer.`;

const correctAnswers = {
  q1: 'Navigating without clear skies',
  q2: 'China invented it first',
  q3Keywords: ['sun', 'star', 'sky', 'skies', 'cloudy', 'navigate'],
};


function checkAnswers(userAnswers) {
  let score = 0;
  const total = 3;

  if ((userAnswers.q1 || '').toLowerCase() === correctAnswers.q1.toLowerCase()) score++;
  if ((userAnswers.q2 || '').toLowerCase() === correctAnswers.q2.toLowerCase()) score++;

  const answerText = (userAnswers.q3 || '').toLowerCase();
  const keywordMatches = correctAnswers.q3Keywords.filter(keyword =>
    answerText.includes(keyword)
  ).length;

  if (keywordMatches >= correctAnswers.q3Keywords.length / 2) score++;

  return { score, total, passed: score / total >= 0.8 };
}

function App() {
  const [mode, setMode] = useState(null); // 'tutorial' | 'experiment'
  const [page, setPage] = useState('home'); // 'home' | 'reading' | 'quiz' | 'finished' | 'loading'

  const [showSummary, setShowSummary] = useState(null); // true = summary, false = full

  // Tutorial
  const [tutorialReadingTime, setTutorialReadingTime] = useState(null);
  const [tutorialQuizAnswers, setTutorialQuizAnswers] = useState(null);

  // Experiment
  const [experimentText, setExperimentText] = useState('');
  const [experimentSummary, setExperimentSummary] = useState('');
  const [experimentQuestions, setExperimentQuestions] = useState([]);
  const [experimentQuizAnswers, setExperimentQuizAnswers] = useState(null);
  const [experimentReadingTime, setExperimentReadingTime] = useState(null);

  function startTutorial() {
    setMode('tutorial');
    setPage('reading');
  }

  function finishTutorialReading(time) {
    setTutorialReadingTime(time);
    setPage('quiz');
  }

  function submitTutorialQuiz(answers) {
    const result = checkAnswers(answers);
    setTutorialQuizAnswers({ ...answers, ...result });
    setPage('finished');
  }

  async function startExperiment() {
    setMode('experiment');
    setPage('loading');
    const useSummary = Math.random() < 0.5;
    setShowSummary(useSummary);

    try {
      const passageRes = await fetch('/api/generate-passage');
      const passageData = await passageRes.json();
      setExperimentText(passageData.text || '');

      if (useSummary) {
        const summaryRes = await fetch('/api/generate-summary', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ text: passageData.text }),
        });
        const summaryData = await summaryRes.json();
        setExperimentSummary(summaryData.summary || '');
      }

      const questionsRes = await fetch('/api/generate-questions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: passageData.text }),
      });
      const questionsData = await questionsRes.json();
      setExperimentQuestions(questionsData.questions || []);

      setPage('reading');
    } catch (err) {
      alert('Failed to load experiment data. Please try again.');
      setMode(null);
      setPage('home');
      console.error(err);
    }
  }

  function finishExperimentReading(time) {
    setExperimentReadingTime(time);
    setPage('quiz');
  }

  function submitExperimentQuiz(answers) {
    const result = checkAnswers(answers);
    setExperimentQuizAnswers({ ...answers, ...result });
    setPage('finished');
  }

  function backToHome() {
    setMode(null);
    setPage('home');
    setShowSummary(null);
    setTutorialReadingTime(null);
    setTutorialQuizAnswers(null);
    setExperimentText('');
    setExperimentSummary('');
    setExperimentQuestions([]);
    setExperimentQuizAnswers(null);
    setExperimentReadingTime(null);
  }

  return (
    <>
      {page === 'home' && (
        <Home onStartTutorial={startTutorial} onStartExperiment={startExperiment} />
      )}

      {mode === 'tutorial' && page === 'reading' && (
        <TutorialReading text={tutorialText} onFinish={finishTutorialReading} />
      )}

      {mode === 'tutorial' && page === 'quiz' && (
        <TutorialQuiz onSubmit={submitTutorialQuiz} />
      )}

      {mode === 'tutorial' && page === 'finished' && (
        <TutorialResult
          quizAnswers={tutorialQuizAnswers}
          readingTime={tutorialReadingTime}
          onBackHome={backToHome}
        />
      )}

      {mode === 'experiment' && page === 'loading' && (
        <div className="min-h-screen flex items-center justify-center p-6">
          <p className="text-xl text-gray-700">Loading experiment...</p>
        </div>
      )}

      {mode === 'experiment' && page === 'reading' && (
        showSummary ? (
          <ExperimentSummary summary={experimentSummary} onFinish={finishExperimentReading} />
        ) : (
          <ExperimentFullText text={experimentText} onFinish={finishExperimentReading} />
        )
      )}

      {mode === 'experiment' && page === 'quiz' && (
        <ExperimentQuiz questions={experimentQuestions} onSubmit={submitExperimentQuiz} />
      )}

      {mode === 'experiment' && page === 'finished' && (
        <ExperimentResult
          quizAnswers={experimentQuizAnswers}
          readingTime={experimentReadingTime}
          onBackHome={backToHome}
        />
      )}
    </>
  );
}

export default App;
