import React from 'react';
import './LoadingScreen.css';

const LoadingScreen = () => {
  return (
    <div className="loading-container">
      <svg className="track" viewBox="0 0 1000 200">
        <path
          id="motionPath"
          d="M 50 150 Q 250 0 500 100 Q 750 200 950 50"
          fill="transparent"
          stroke="#ddd"
          strokeWidth="4"
        />
        <g transform="scale(0.2)">
  <path d="M256 96c-26.5 0-48 21.5-48 48h-80c-17.7 0-32 14.3-32 32v112h16c0-35.3 28.7-64 64-64s64 28.7 64 64h128c0-35.3 28.7-64 64-64s64 28.7 64 64h16V176c0-17.7-14.3-32-32-32h-80c0-26.5-21.5-48-48-48h-64z" fill="#007bff">
    <animateMotion repeatCount="indefinite" dur="4s">
      <mpath href="#motionPath" />
    </animateMotion>
  </path>
</g>

      </svg>
      <div className="loading-text">Generating your reading content...</div>
    </div>
  );
};

export default LoadingScreen;
