import React, { useState } from 'react';

function ExperimentQuiz({ questions, onSubmit }) {
  const [answers, setAnswers] = useState({});

  const handleChange = (e, index) => {
    const updatedAnswers = { ...answers, [`q${index + 1}`]: e.target.value };
    setAnswers(updatedAnswers);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(answers);
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="max-w-3xl mx-auto p-6 flex flex-col space-y-6"
    >
      <h2 className="text-2xl font-bold text-gray-800">Experiment Quiz</h2>

      {questions.map((q, index) => (
        <div key={index} className="flex flex-col space-y-2">
          <label className="font-medium text-gray-700">{q}</label>
          <textarea
            value={answers[`q${index + 1}`] || ''}
            onChange={(e) => handleChange(e, index)}
            className="p-2 border border-gray-300 rounded-md"
            rows={3}
          />
        </div>
      ))}

      <button
        type="submit"
        className="self-start px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700"
      >
        Submit Answers
      </button>
    </form>
  );
}

export default ExperimentQuiz;
