import React from 'react';
import { motion } from 'framer-motion';
import { CarFront } from 'lucide-react';

const LoadingScreen = () => {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-white">
      <h2 className="text-xl text-gray-700 mb-6">Generating your custom reading race...</h2>

      <svg viewBox="0 0 400 200" className="w-[90%] max-w-md h-40">
        <path
          id="track"
          d="M 20 100 C 100 0, 300 200, 380 100"
          fill="none"
          stroke="#e5e7eb"
          strokeWidth="4"
          strokeDasharray="8 6"
        />
        <motion.g>
          <motion.foreignObject
            width="24"
            height="24"
            style={{ overflow: 'visible' }}
            animate={{
              offsetDistance: ["0%", "100%"],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "linear"
            }}
          >
            <CarFront className="text-blue-600 w-6 h-6" />
          </motion.foreignObject>
        </motion.g>
      </svg>
    </div>
  );
};

export default LoadingScreen;
