import React, { useState, useEffect } from 'react';

const Profile = ({ onBackHome, onLogout }) => {
  const [displayName, setDisplayName] = useState('');
  const [optIn, setOptIn] = useState(false);
  const [saving, setSaving] = useState(false);
  const [saved, setSaved] = useState(false);

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setDisplayName(storedUser.userName || '');
      setOptIn(!!storedUser.leaderboardOptIn);
    }
  }, []);

  const handleSave = async () => {
    const updatedUser = {
      ...JSON.parse(localStorage.getItem('user')),
      userName: displayName,
      leaderboardOptIn: optIn,
    };

    setSaving(true);
    try {
      // Save locally
      localStorage.setItem('user', JSON.stringify(updatedUser));

      // Optional: Save to backend
      await fetch('https://read-racer.azurewebsites.net/api/updateUserSettings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userId: updatedUser.uuid,
          userName: displayName,
          leaderboardOptIn: optIn,
        }),
      });

      setSaved(true);
      setTimeout(() => setSaved(false), 2000);
    } catch (err) {
      alert('Failed to save settings');
      console.error(err);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto p-6 mt-10 bg-white rounded-2xl shadow-md border border-gray-200">
      <h2 className="text-2xl font-bold text-gray-800 mb-6">Profile Settings</h2>

      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-1">
          Displayed Name (for leaderboard)
        </label>
        <input
          type="text"
          value={displayName}
          onChange={(e) => setDisplayName(e.target.value)}
          className="w-full px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          placeholder="Enter your name"
        />
      </div>

      <div className="flex items-center mb-6">
        <input
          id="optIn"
          type="checkbox"
          checked={optIn}
          onChange={() => setOptIn(!optIn)}
          className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
        />
        <label htmlFor="optIn" className="ml-2 text-sm text-gray-700">
          I want to appear on the global leaderboard
        </label>
      </div>

      <div className="flex space-x-3">
        <button
          onClick={handleSave}
          disabled={saving}
          className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition disabled:opacity-50"
        >
          {saving ? 'Saving...' : 'Save'}
        </button>

        <button
          onClick={onLogout}
          className="bg-gray-300 text-gray-800 px-6 py-2 rounded-lg hover:bg-gray-400 transition"
        >
          Sign Out
        </button>

        <button
          onClick={onBackHome}
          className="ml-auto text-blue-600 hover:underline text-sm"
        >
          Back to Home
        </button>
      </div>

      {saved && <p className="mt-4 text-green-600 text-sm">Settings saved!</p>}
    </div>
  );
};

export default Profile;
