import React, { useState, useEffect } from 'react';

function TutorialReading({ text, onFinish }) {
  const [time, setTime] = useState(0);
  const [isReading, setIsReading] = useState(true);

  useEffect(() => {
    if (!isReading) return;

    const timer = setInterval(() => setTime((t) => t + 1), 1000);

    return () => clearInterval(timer);
  }, [isReading]);

  const handleFinish = () => {
    setIsReading(false);
    onFinish(time);
  };

  return (
    <div className="flex flex-col max-w-3xl mx-auto p-6 space-y-6">
      <div
        className="border rounded p-4 h-72 overflow-y-auto bg-white shadow"
        style={{ whiteSpace: 'pre-wrap' }} // keeps line breaks
      >
        {text}
      </div>
      <p className="text-gray-600">Time elapsed: {time} seconds</p>
      <button
        onClick={handleFinish}
        className="self-start px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Finished Reading
      </button>
    </div>
  );
}

export default TutorialReading;
