import React, { useState } from 'react';

const TutorialQuiz = ({ onSubmit }) => {
  const [answers, setAnswers] = useState({
    q1: '',
    q2: '',
    q3: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setAnswers((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(answers);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-6 py-12 bg-white">
      <form onSubmit={handleSubmit} className="max-w-3xl w-full bg-gray-100 p-6 rounded-xl shadow space-y-6">
        <h2 className="text-2xl font-semibold text-gray-800">Tutorial Quiz</h2>

        {/* Q1 */}
        <div>
          <p className="font-medium mb-2">1. What problem did the compass help solve for sailors?</p>
          {['A. Cooking on ships', 'B. Finding treasure', 'C. Navigating without clear skies', 'D. Communicating with land'].map((choice, idx) => (
            <label key={idx} className="block">
              <input
                type="radio"
                name="q1"
                value={choice[0].toLowerCase()}
                checked={answers.q1 === choice[0].toLowerCase()}
                onChange={handleChange}
                className="mr-2"
              />
              {choice}
            </label>
          ))}
        </div>

        {/* Q2 */}
        <div>
          <p className="font-medium mb-2">2. Where was the compass first invented?</p>
          {['A. Egypt', 'B. Greece', 'C. China', 'D. India'].map((choice, idx) => (
            <label key={idx} className="block">
              <input
                type="radio"
                name="q2"
                value={choice[0].toLowerCase()}
                checked={answers.q2 === choice[0].toLowerCase()}
                onChange={handleChange}
                className="mr-2"
              />
              {choice}
            </label>
          ))}
        </div>

        {/* Q3 */}
        <div>
          <p className="font-medium mb-2">3. Name one way people used to navigate before the invention of the compass.</p>
          <input
            type="text"
            name="q3"
            value={answers.q3}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded-lg"
            placeholder="Type your answer..."
          />
        </div>

        <button
          type="submit"
          className="px-6 py-3 bg-green-600 text-white rounded-xl hover:bg-green-700 transition"
        >
          Submit Answers
        </button>
      </form>
    </div>
  );
};

export default TutorialQuiz;
