import React, { useState } from 'react';

function ExperimentQuiz({ questions, onSubmit }) {
  const [answers, setAnswers] = useState({});

  const handleChange = (questionId, value) => {
    setAnswers(prev => ({ ...prev, [questionId]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(answers);
  };

  return (
    <div className="max-w-3xl mx-auto p-6 flex flex-col space-y-6">
      <h2 className="text-2xl font-bold text-gray-800 mb-4">Quiz</h2>
      <form onSubmit={handleSubmit} className="space-y-6">

        {questions.map(({ id, type, question, choices }) => (
          <div key={id} className="bg-white p-6 rounded shadow">
            <p className="mb-2 font-semibold text-gray-700">{question}</p>

            {type === 'multiple' && choices && (
              <div className="space-y-2">
                {choices.map((choice, idx) => (
                  <label key={idx} className="flex items-center space-x-2 cursor-pointer">
                    <input
                      type="radio"
                      name={id}
                      value={choice}
                      checked={answers[id] === choice}
                      onChange={() => handleChange(id, choice)}
                      className="cursor-pointer"
                    />
                    <span>{choice}</span>
                  </label>
                ))}
              </div>
            )}

            {(type === 'short' || type === 'long') && (
              <textarea
                name={id}
                value={answers[id] || ''}
                onChange={(e) => handleChange(id, e.target.value)}
                rows={type === 'short' ? 3 : 6}
                className="w-full border border-gray-300 rounded p-2 resize-none"
                placeholder="Type your answer here..."
                required
              />
            )}
          </div>
        ))}

        <button
          type="submit"
          className="self-start px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Submit Quiz
        </button>
      </form>
    </div>
  );
}

export default ExperimentQuiz;
