import React, { useState } from 'react';

const Home = ({ onStartTutorial, onStartExperiment }) => {
  const [userEmail, setUserEmail] = useState('');

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-4 text-center bg-gray-50">
      <h1 className="text-4xl font-bold text-gray-800 mb-4">Welcome to Read Racer</h1>
      <p className="text-lg text-gray-600 max-w-xl mb-8">
        Test your reading speed and comprehension. Choose between a guided tutorial or a real-time experiment with AI-generated content.
      </p>

      <div className="mb-6 w-full max-w-md">
        <label htmlFor="email" className="block text-left font-medium text-gray-700 mb-1">
          Optional: Enter your email
        </label>
        <input
          id="email"
          type="email"
          value={userEmail}
          onChange={(e) => setUserEmail(e.target.value)}
          className="w-full border rounded-md p-2 text-gray-800"
          placeholder="example@example.com"
        />
      </div>

      <div className="flex flex-col sm:flex-row gap-4">
        <button
          onClick={onStartTutorial}
          className="px-6 py-3 bg-blue-600 text-white rounded-xl shadow hover:bg-blue-700 transition"
        >
          Start Tutorial
        </button>

        <button
          onClick={onStartExperiment}
          className="px-6 py-3 bg-green-600 text-white rounded-xl shadow hover:bg-green-700 transition"
        >
          Start Experiment
        </button>
      </div>
    </div>
  );
};

export default Home;
