import React, { useState } from 'react';

const questions = [
  {
    id: 'q1',
    type: 'multiple',
    question: 'What problem did the compass help solve for sailors?',
    choices: [
      'Cooking on ships',
      'Finding treasure',
      'Navigating without clear skies',
      'Communicating with land',
    ],
  },
  {
    id: 'q2',
    type: 'multiple',
    question: 'Where was the compass first invented?',
    choices: ['Egypt', 'Greece', 'China', 'India'],
  },
  {
    id: 'q3',
    type: 'short',
    question: 'Name one way people used to navigate before the invention of the compass.',
  },
];

function TutorialQuiz({ onSubmit }) {
  const [answers, setAnswers] = useState({});

  const handleChange = (id, value) => {
    setAnswers((prev) => ({ ...prev, [id]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(answers);
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-3xl mx-auto p-6 space-y-8 bg-white rounded shadow">
      {questions.map(({ id, type, question, choices }) => (
        <div key={id}>
          <p className="mb-3 font-semibold">{question}</p>
          {type === 'multiple' && (
            <div className="space-y-2">
              {choices.map((choice, i) => (
                <label key={i} className="block cursor-pointer">
                  <input
                    type="radio"
                    name={id}
                    value={choice.toLowerCase()}
                    checked={answers[id] === choice.toLowerCase()}
                    onChange={() => handleChange(id, choice.toLowerCase())}
                    className="mr-2"
                    required
                  />
                  {choice}
                </label>
              ))}
            </div>
          )}
          {type === 'short' && (
            <textarea
              className="w-full p-2 border rounded"
              rows={3}
              value={answers[id] || ''}
              onChange={(e) => handleChange(id, e.target.value)}
              required
              placeholder="Type your answer here..."
            />
          )}
        </div>
      ))}

      <button
        type="submit"
        className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Submit Answers
      </button>
    </form>
  );
}

export default TutorialQuiz;
