import React, { useEffect, useState } from 'react';

const ExperimentSummary = ({ summary, onFinish }) => {
  const [startTime, setStartTime] = useState(null);

  useEffect(() => {
    // Start timing when component mounts
    setStartTime(Date.now());
  }, []);

  const handleFinishReading = () => {
    const endTime = Date.now();
    const timeInSeconds = Math.round((endTime - startTime) / 1000);
    onFinish(timeInSeconds);
  };

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col justify-center items-center px-4 py-12">
      <div className="max-w-3xl w-full bg-white rounded-xl shadow-md p-8">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-6">Experiment: Summary Passage</h1>
        
        <div className="prose max-w-none text-gray-800 whitespace-pre-wrap mb-8">
          {summary}
        </div>

        <div className="text-center">
          <button
            onClick={handleFinishReading}
            className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition"
          >
            Finished Reading
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExperimentSummary;
