import React from 'react';

const TutorialResult = ({ quizAnswers, readingTime, onBackHome }) => {
  if (!quizAnswers) {
    return (
      <div className="min-h-screen flex items-center justify-center p-6">
        <p className="text-gray-700">No quiz data found.</p>
      </div>
    );
  }
  
  const { score, total, passed } = quizAnswers;

  return (
    <div className="min-h-screen flex flex-col items-center justify-center px-6 py-12 bg-white">
      <div className="max-w-md w-full bg-gray-100 p-8 rounded-xl shadow text-center">
        <h2 className="text-3xl font-semibold mb-4 text-gray-800">Tutorial Results</h2>
        <p className="text-lg mb-2">
          You scored <span className="font-bold">{score}</span> out of <span className="font-bold">{total}</span>.
        </p>
        <p className={`mb-6 text-xl font-semibold ${passed ? 'text-green-600' : 'text-red-600'}`}>
          {passed ? 'Great job! You passed the tutorial.' : 'Keep practicing!'}
        </p>

        {readingTime && (
          <p className="mb-6 text-gray-700">
            Reading Time: <span className="font-mono">{readingTime} seconds</span>
          </p>
        )}

        <button
          onClick={onBackHome}
          className="px-6 py-3 bg-blue-600 text-white rounded-xl hover:bg-blue-700 transition"
        >
          Back to Home
        </button>
      </div>
    </div>
  );
};

export default TutorialResult;
